//             if (value === 0.16) {
//               return value;
//             }
//           },
//         },
//       },
//     ],
//   },
// }

const options = {
  type: "line",
  data: {
    labels: ["January", "February", "March", "April", "May", "June", "July"],
    datasets: [
      {
        label: "My First dataset",
        backgroundColor: "blue",
        borderColor: "blue",
        fill: false,
        data: [
          randomScalingFactor(),
          randomScalingFactor(),
          randomScalingFactor(),
          randomScalingFactor(),
          randomScalingFactor(),
          randomScalingFactor(),
          randomScalingFactor(),
        ],
      },
      {
        label: "My Second dataset",
        backgroundColor: "green",
        borderColor: "green",
        fill: false,
        data: [
          randomScalingFactor(),
          randomScalingFactor(),
          randomScalingFactor(),
          randomScalingFactor(),
          randomScalingFactor(),
          randomScalingFactor(),
          randomScalingFactor(),
        ],
      },
    ],
  },
  options: {
    responsive: true,
    title: {
      display: true,
      text: "Chart.js Line Chart - Logarithmic",
    },
    scales: {
      x: {
        display: true,
      },
      y: {
        display: true,
        type: "logarithmic",
      },
    },
    plugins: {
      annotation: {
        annotations: {
          myHorizontalLine: {
            drawTime: "afterDraw",
            type: "line",
            scaleID: "y",
            value: randomScalingFactor(),
            borderColor: "black",
            borderWidth: 5,
            label: {
              backgroundColor: "red",
              content: "Test Label",
              enabled: true,
            },
            click(context) {
              // The annotation is is bound to the `this` variable
              console.log("Annotation", context);
            },
          },
          myVerticalLine: {
            drawTime: "afterDraw",
            type: "line",
            scaleID: "x",
            value: "May",
            borderColor: "black",
            borderWidth: 5,
            label: {
              backgroundColor: "red",
              content: "Test Label",
              enabled: true,
            },
            click(context) {
              // The annotation is is bound to the `this` variable
              console.log("Annotation", context);
            },
          },
          myBox: {
            drawTime: "beforeDatasetsDraw",
            type: "box",
            xScaleID: "x",
            yScaleID: "y",
            xMin: "February",
            xMax: "April",
            yMin: randomScalingFactor(),
            yMax: randomScalingFactor(),
            backgroundColor: "rgba(101, 33, 171, 0.5)",
            borderColor: "rgb(101, 33, 171)",
            borderWidth: 1,
            click(context) {
              console.log("Box", context);
            },
          },
        },
      },
    },
  },
};

const initialData = {
  labels: [
    "(time unknown)",
    "12:42 AM",
    "(time unknown)",
    "2:58 AM",
    "(time unknown)",
    "(time unknown)",
  ],
  datasets: [
    {
      data: [null, null, null, 0.16, null, null],
      label: "One measurement",
      backgroundColor: "#78350F",
      borderColor: "#78350F",
      fill: false,
      pointRadius: [0, 0, 0, 7, 0],
      pointHoverBorderWidth: [0, 0, 0, 8, 0],
    },
  ],
};

const Index = () => {
  const [scenarioOneShown, setScenarioOneShown] = useState(false);
  const [scenarioTwoShown, setScenarioTwoShown] = useState(false);
  const [scenarioThreeShown, setScenarioThreeShown] = useState(false);
  const [data, setData] = useState(initialData);

  const applyScenarioOne = () => {
    if (!scenarioOneShown) {
      let updatedDatasets = [...data.datasets, scenarioOne];
      setData({ ...data, datasets: updatedDatasets });
      setScenarioOneShown(true);
    }
  };

  const applyScenarioTwo = () => {
    if (!scenarioTwoShown) {
      let updatedDatasets = [...data.datasets, scenarioTwo];
      setData({ ...data, datasets: updatedDatasets });
      setScenarioTwoShown(true);
    }
  };

  const applyScenarioThree = () => {
    if (!scenarioThreeShown) {
      let updatedDatasets = [...data.datasets, scenarioThree];
      setData({ ...data, datasets: updatedDatasets });
      setScenarioThreeShown(true);
    }
  };

  return (
    <Main
      meta={
        <Meta
          title="Cannon Demonstrative"
          description="The demonstrative for Dr. Robert Cannon - Gardner Consulting."
        />
      }
    >
      <div className="mx-16 my-10">
        <div className="flex w-full">
          <Line data={data} width={600} height={280} options={options}></Line>
        </div>
        <div className="flex justify-center w-full mt-12">
          <button
            className={
              "px-4 py-2 font-semibold bg-transparent border rounded hover:text-white  " +
              (!scenarioOneShown
                ? "opacity-25 text-green-700 border-green-500 hover:bg-green-500 hover:border-transparent"
                : "text-white bg-green-500 border-green-500 hover:border-transparent ")
            }
            onClick={() => applyScenarioOne()}
          >
            Scenario one
          </button>
          <button
            className={
              "px-4 py-2 mx-16 font-semibold bg-transparent border rounded hover:text-white  " +
              (!scenarioTwoShown
                ? "opacity-25 text-blue-700 border-blue-500 hover:bg-blue-500 hover:border-transparent"
                : "text-white bg-blue-500 border-blue-500 hover:border-transparent ")
            }
            onClick={() => applyScenarioTwo()}
          >
            Scenario two
          </button>
          <button
            className={
              "px-4 py-2 font-semibold bg-transparent border rounded hover:text-white  " +
              (!scenarioThreeShown
                ? "opacity-25 text-red-700 border-red-500 hover:bg-red-500 hover:border-transparent"
                : "text-white bg-red-500 border-red-500 hover:border-transparent ")
            }
            onClick={() => applyScenarioThree()}
          >
            Scenario three
          </button>
        </div>
      </div>
    </Main>
  );
};

export default Index;
