export const oneMeasurement = {
  order: 1,
  data: [null, null, null, 0.16, null, null],
  label: "One measurement",
  backgroundColor: "#92400E",
  borderColor: "#92400E",
  showLine: false,
  fill: false,
  pointRadius: [0, 0, 0, 10, 0],
  pointHoverBorderWidth: [0, 0, 0, 15, 0],
};

export const scenarioThree = {
  order: 1,
  data: [0.0, 0.162, 0.474, 0.16, 0.081, 0.04],
  label: "Possible Curve #2",
  backgroundColor: "#EF4444",
  borderColor: "#EF4444",
  pointRadius: [0, 0, 10, 10, 0, 0],
  pointHoverBorderWidth: [0, 0, 10, 15, 0, 0],
  fill: false,
};

export const scenarioOne = {
  order: 2,
  data: [0.0, 0.09, 0.356, 0.16, 0.072, 0.04],
  label: "Typical curve",
  backgroundColor: "#10B981",
  borderColor: "#10B981",
  pointRadius: [0, 0, 10, 10, 0, 0],
  pointHoverBorderWidth: [0, 0, 10, 15, 0, 0],
  fill: false,
};

export const scenarioTwo = {
  order: 1,
  data: [0.0, 0.072, 0.224, 0.16, 0.062, 0.04],
  label: "Possible Curve #1",
  backgroundColor: "#3B82F6",
  borderColor: "#3B82F6",
  pointRadius: [0, 0, 10, 10, 0, 0],
  pointHoverBorderWidth: [0, 0, 10, 15, 0, 0],
  fill: false,
};

export const initialData = {
  labels: [
    "(time unknown)",
    "12:42 AM",
    "(time unknown)",
    "2:58 AM",
    "(time unknown)",
    "(time unknown)",
  ],
  datasets: [],
};

export let annotations = [
  {
    type: "line",
    mode: "horizontal",
    scaleID: "y-axis-0",
    value: 0.16,
    borderColor: "rgba(0, 0, 0, 0.1)",
    borderWidth: 1,
    borderDash: [10, 5],
    showLine: false,
    label: {
      enabled: false,
      content: "Test label",
    },
  },
  {
    type: "line",
    mode: "vertical",
    scaleID: "x-axis-0",
    value: "2:58 AM",
    borderColor: "rgba(0, 0, 0, 0.1)",
    borderWidth: 1,
    borderDash: [10, 5],
    showLine: false,
    label: {
      enabled: false,
      content: "Test label",
    },
  },
];

export const config: any = {
  responsive: true,
  maintainAspectRatio: true,

  legend: {
    position: "right",
    labels: {
      fontSize: 16,
      fontColor: "#374151",
    },
  },
  tooltips: { enabled: false },
  hover: { mode: null },
  title: {
    display: true,
    text: "BAC Analysis - Robert Cannon M.D.",
    fontSize: 26,
    fontFamily: "'Helvetica'",
    fontColor: "#1F2937",
  },
  annotation: {},
  scales: {
    xAxes: [
      {
        scaleLabel: {
          display: true,
          fontSize: 20,
          fontColor: "#374151",
          labelString: "Known Times (AM)",
        },
        gridLines: {
          drawOnChartArea: false,
          fontColor: "rgba(0 , 0, 0, 0.65)",
        },
        ticks: {
          display: false,
          callback: function (value: String) {
            if (value === "2:58 AM") {
              return value;
            }
          },
          fontSize: 20,
          fontColor: "#374151",
        },
      },
    ],
    yAxes: [
      {
        scaleLabel: {
          display: true,
          fontSize: 20,
          fontColor: "#374151",
          labelString: "Blood Alcohol Concentration (g/ml)",
        },
        gridLines: {
          drawOnChartArea: false,
        },
        ticks: {
          display: false,
          min: 0,
          max: 0.5,
          stepSize: 0.04,
          fontSize: 20,
          fontColor: "#374151",
          callback: function (value) {
            if (value === 0.16) {
              return value + "%";
            }
          },
        },
      },
    ],
  },
};
